  "query" : "SELECT ChannelId FROM \"sdk-${region}-realtime\" WHERE (__time >= '${__from:date:iso}' AND __time <= '${__to:date:iso}') AND (CustomerId = '${customer_id}') AND (TIMESTAMPDIFF(DAY, MILLIS_TO_TIMESTAMP(${__from}), CURRENT_TIMESTAMP) <= 1) GROUP BY ChannelId UNION ALL SELECT ChannelId FROM \"sdk-${region}-historical-minute\" WHERE (__time >= '${__from:date:iso}' AND __time <= '${__to:date:iso}') AND (CustomerId = '${customer_id}') AND (TIMESTAMPDIFF(DAY, MILLIS_TO_TIMESTAMP(${__from}), CURRENT_TIMESTAMP) > 1) GROUP BY ChannelId",
  "context":  {
    "skipEmptyBuckets": false,
    "useCache": true,
    "useResultLevelCache": true
  }
}

