{
  "query" : "SELECT MAX(SumDeviceCount) AS \"MaxDeviceCount\" FROM (SELECT \"timestamp\", SUM(DeviceCount) AS \"SumDeviceCount\" FROM (SELECT TIME_FLOOR(__time, 'P${iso_period}') AS \"timestamp\", COUNT(DISTINCT SessionId) AS \"DeviceCount\" FROM \"sdk-${region}-${data_layer}\" WHERE VideoResolutionHeight > 0 AND Bitrate IS NOT NULL AND CustomerId IN (${customer_ids:singlequote}) AND (CAST('${channel_id}' AS int) = -1 OR ChannelId IN ('${channel_id}')) AND (CAST('${stream_uri}' AS int) = -1 OR MaskedStreamUri IN ('${stream_uri}')) AND (CAST('${bitrate}' AS int) = -1 OR Bitrate IN ('${bitrate}')) AND (CAST('${stream_state}' AS int) = -1 OR StreamState IN ('${stream_state}')) AND __time >= '${__from:date:iso}' AND __time <= '${__to:date:iso}' GROUP BY 1, VideoResolutionHeight) GROUP BY 1)",
  "context":  {
    "skipEmptyBuckets": false,
    "useApproximateCountDistinct": ${accuracy},
    "useCache": true,
    "useResultLevelCache": true
  }
}

