{
  "query" : "SELECT TIME_FLOOR(__time, 'P${iso_period}') AS \"timestamp\", CONCAT(VideoResolutionHeight, 'p') AS \"VideoResolutions\", COUNT(DISTINCT SessionId) AS \" \" FROM \"sdk-${region}-${data_layer}\" WHERE PlayerState IN ('playing', 'buffering') AND VideoResolutionHeight > 0 AND VideoResolutionWidth > 0 AND (((ViewportHeight - VideoResolutionHeight) / CAST(VideoResolutionHeight AS FLOAT) <= 0.01) OR ((ViewportWidth - VideoResolutionWidth) / CAST(VideoResolutionWidth AS FLOAT) <= 0.01)) AND CustomerId IN (${customer_ids:singlequote}) AND (CAST('${channel_id}' AS int) = -1 OR ChannelId IN ('${channel_id}')) AND (CAST('${stream_uri}' AS int) = -1 OR MaskedStreamUri IN ('${stream_uri}')) AND (CAST('${bitrate}' AS int) = -1 OR Bitrate IN ('${bitrate}')) AND (CAST('${stream_state}' AS int) = -1 OR StreamState IN ('${stream_state}')) AND __time >= '${__from:date:iso}' AND __time <= '${__to:date:iso}' GROUP BY 1, 2",
