    "useCache": true,
    "useResultLevelCache": true
  }
}

{
  "query" : "SELECT \"timestamp\", CONCAT(\"VideoResolutionHeight\", 'p') AS \"video_res\", CAST(SUM(\"audience\") AS FLOAT) AS \"suboptimal_count\" FROM(SELECT TIME_FLOOR(__time, 'P${iso_period}') AS \"timestamp\", VideoResolutionHeight, COUNT(DISTINCT SessionId) AS \"audience\", MAX(VideoResolutionHeight) OVER (PARTITION BY TIME_FLOOR(__time, 'P${iso_period}'), MaskedStreamUri) AS \"max_res_height\" FROM \"sdk-${region}-${data_layer}\" WHERE PlayerState IN ('playing', 'buffering') AND (((ViewportHeight - VideoResolutionHeight) / (CASE WHEN VideoResolutionHeight = 0 THEN 1.0 ELSE CAST(VideoResolutionHeight AS FLOAT) END) > 0.01) AND ((ViewportWidth - VideoResolutionWidth) / (CASE WHEN VideoResolutionWidth = 0 THEN 1.0 ELSE CAST(VideoResolutionWidth AS FLOAT) END) > 0.01)) AND RepresentationId NOT LIKE '1 of%' AND RepresentationId != '0' AND CustomerId IN (${customer_ids:singlequote}) AND (CAST('${channel_id}' AS int) = -1 OR ChannelId IN ('${channel_id}')) AND (CAST('${stream_uri}' AS int) = -1 OR MaskedStreamUri IN ('${stream_uri}')) AND (CAST('${bitrate}' AS int) = -1 OR Bitrate IN ('${bitrate}')) AND (CAST('${stream_state}' AS int) = -1 OR StreamState IN ('${stream_state}')) AND __time >= '${__from:date:iso}' AND __time <= '${__to:date:iso}' GROUP BY 1, MaskedStreamUri, VideoResolutionHeight) WHERE VideoResolutionHeight < max_res_height GROUP BY 1, VideoResolutionHeight",
  "context":  {
    "skipEmptyBuckets": false,
    "useApproximateCountDistinct": ${accuracy},
    "useCache": true,
    "useResultLevelCache": true,
    "enableWindowing": true
  }
}

{
  "query" : "SELECT \"timestamp\", APPROX_QUANTILE_DS(MaxStartingTime, 0.25) AS \"Q1\", APPROX_QUANTILE_DS(MaxStartingTime, 0.50) AS \"Median\", APPROX_QUANTILE_DS(MaxStartingTime, 0.75) AS \"Q3\", APPROX_QUANTILE_DS(MaxStartingTime, 0.975) AS \"975th\" FROM (SELECT TIME_FLOOR(__time, 'P${iso_period}') AS \"timestamp\", MAX(StartingTime) AS \"MaxStartingTime\" FROM \"sdk-${region}-${data_layer}\" WHERE PlayerState = 'playing' AND StartingTime IS NOT NULL AND CustomerId IN (${customer_ids:singlequote}) AND (CAST('${channel_id}' AS int) = -1 OR ChannelId IN ('${channel_id}')) AND (CAST('${stream_uri}' AS int) = -1 OR MaskedStreamUri IN ('${stream_uri}')) AND (CAST('${bitrate}' AS int) = -1 OR Bitrate IN ('${bitrate}')) AND (CAST('${stream_state}' AS int) = -1 OR StreamState IN ('${stream_state}')) AND __time >= '${__from:date:iso}' AND __time <= '${__to:date:iso}' GROUP BY 1, SessionId) GROUP BY 1",
  "context":  {
    "skipEmptyBuckets": false,
    "useCache": true,
    "useResultLevelCache": true,
    "timeout": 240000
  }
}

