{
  "query" : "SELECT \"timestamp\", AVG(BufferingRatio) AS \"AbsAvgBufferingRatio\" FROM (SELECT TIME_FLOOR(__time, 'P${iso_period}') AS \"timestamp\", CASE WHEN (MAX(PlayingTime) + MAX(BufferingTime)) = 0 THEN 0 ELSE MAX(BufferingTime) / (MAX(PlayingTime) + MAX(BufferingTime) * 1.0) END AS \"BufferingRatio\" FROM \"sdk-${region}-${data_layer}\" WHERE BufferingTime <= 600000 AND CustomerId IN (${customer_ids:singlequote}) AND (CAST('${channel_id}' AS int) = -1 OR ChannelId IN ('${channel_id}')) AND (CAST('${stream_uri}' AS int) = -1 OR MaskedStreamUri IN ('${stream_uri}')) AND (CAST('${bitrate}' AS int) = -1 OR Bitrate IN ('${bitrate}')) AND (CAST('${stream_state}' AS int) = -1 OR StreamState IN ('${stream_state}')) AND __time >= '${__from:date:iso}' AND __time <= '${__to:date:iso}' GROUP BY 1, SessionId) GROUP BY 1",
